<?phpclass sql{/*	private $lsql = 'kaapstadsite';	private $psql = 'r0NvcWl1q4u9';	private $hsql = 'mysql5-2.pro';	private $bsql = 'kaapstadsite';*/	private $lsql = 'root';	private $psql = 'root';	private $hsql = 'localhost';	private $bsql = 'kaapstadesite';	public function __construct()	{		mysql_connect($this->hsql, $this->lsql, $this->psql);		mysql_select_db($this->bsql);		//necessaire seulement après la connection		mysql_query("SET NAMES 'utf8'");	}	public function __destruct()	{		mysql_close();	}	// Action sur le base	public function lire($table, $condition=1, $tab=TRUE)	{		$table = addslashes($table);		$req = mysql_query("SELECT * FROM `$table` WHERE $condition")or die(mysql_error());		if ($tab == TRUE)			return mysql_fetch_array($req);		else			return $req;	}	public function requete($data, $tab=true)	{		$req = mysql_query($data)or die(mysql_error());		if ($tab == TRUE)			return mysql_fetch_array($req);		else			return $req;	}	public function ecrire($table, $valeur)	{		mysql_query("INSERT INTO `$table` VALUES($valeur)")or die(mysql_error());	}	public function update($table, $valeur, $condition)	{		mysql_query("UPDATE `$table` SET $valeur WHERE $condition")or die(mysql_error());	}}$sql = new sql;?>